import { GameOptions } from './game-options';
import { SpriteBase } from './sprite-base';

export class WarriorSprite extends SpriteBase {
  // extends Phaser.Physics.Arcade.Sprite {

  constructor(protected scene: Phaser.Scene) {
    super(scene);

    this.sprite = this.scene.add.sprite(
      GameOptions.gameSize.width / 2,
      GameOptions.gameSize.height / 2,
      'warrior'
    );
    this.initAnimations();
    this.sprite.scale = 0.15;
    this.sprite.play('idle');

    this.scene.physics.add.existing(this.sprite);
    this.getBody().setCollideWorldBounds(true);
  }

  update(): void {
    if (this.cursorKeys.right.isDown) {
      this.sprite.flipX = false;
      this.sprite.anims.play('walk', true);
    } else if (this.cursorKeys.left.isDown) {
      this.sprite.flipX = true;
      this.sprite.anims.play('walk', true);
    } else if (this.spaceKey.isDown) {
      if (this.getBody().onFloor()) {
        this.sprite.body.velocity.y = -400;
      }
    } else {
      this.sprite.anims.play('idle', true);
    }
  }

  private initAnimations() {
    const walkFrames = [];
    const idleFrames = [];

    // warrior frames
    for (let i = 0; i < 10; i++) {
      walkFrames.push({ key: `walk${i}` });
      idleFrames.push({ key: `idle${i}` });
    }

    // keyframes
    this.sprite.anims.create({
      key: 'walk',
      frames: walkFrames,
      frameRate: 10,
      repeat: -1,
    });

    this.sprite.anims.create({
      key: 'idle',
      frames: idleFrames,
      frameRate: 10,
      repeat: -1,
    });
  }
}
